    }
  },
  mounted() {
    window.addEventListener(
      'deviceorientation',
      this.ondeviceorientation,
      false
    )
  },
  destroyed() {
    window.removeEventListener(
      'deviceorientation',
      this.ondeviceorientation,
      false
    )
  },
  methods: {
    ondeviceorientation(e) {
      if (typeof this._alpha !== 'undefined') {
        this.offsetX += (this._gamma - e.gamma) * 0.3
        this.offsetY += (this._beta - e.beta) * 0.3
      }
      this._alpha = e.alpha
      this._beta = e.beta
      this._gamma = e.gamma
    }
  }
}
</script>

